mapper.insert:
insert into board(b_seq,b_email,b_name,b_password,b_title,b_content,
b_ip,b_ref,b_step,b_hit,b_level,b_attach,b_date
)values(
(select ifnull(max(b_seq)+1,1) from board alias_seq),#{b_email,jdbcType=VARCHAR},#{b_name,jdbcType=VARCHAR},
#{b_password,jdbcType=VARCHAR},#{b_title,jdbcType=VARCHAR},#{b_content,jdbcType=VARCHAR},
#{b_ip,jdbcType=VARCHAR},(select ifnull(max(b_seq)+1,1) from board alias_seq),0,
0,#{b_level,jdbcType=INTEGER},#{b_attach,jdbcType=VARCHAR},#{b_date,jdbcType=VARCHAR}
)

mapper.update:
update board set
b_title=#{b_title,jdbcType=VARCHAR},b_content=#{b_content,jdbcType=VARCHAR},
b_attach=#{b_attach,jdbcType=VARCHAR}
where b_seq = #{b_seq}
